import React from "react";
import { withRouter } from "react-router-dom";
import {
  Container,
  Card,
  CardContent,
  withStyles,
  Grid,
  Divider,
  Typography,
  TextField,
  Button,
} from "@material-ui/core";

import KeyboardBackspaceIcon from "@material-ui/icons/KeyboardBackspace";

import { Hidden } from "@material-ui/core";
import ArrowRightIcon from "@material-ui/icons/ArrowRight";
import axios from "axios";
import config from "../../../config";
import Fab from "@material-ui/core/Fab";
import CheckIcon from "@material-ui/icons/Check";
import Fade from "@material-ui/core/Fade";

import CircularProgress from "@material-ui/core/CircularProgress";
import { EdittablesContext } from "../../../contexts/index";
const styles = (theme) => ({
  root: {
    background: theme.palette.mainbackground.default,
  },
  card: {
    background: theme.palette.background.paper,
    boxShadow: "0px 5px 10px #00000014",
    borderRadius: 16,
    opacity: 1,
    margin: "20px 0px",
  },
  text_title: {
    color: "#2A2A2A",
    padding: "6px 0px",
    opacity: 1,
    fontSize: 14,
    margin: "6px 0px",
  },
  card_title: {
    borderBottom: `2px solid ${theme.palette.mainbackground.default}`,
    fontWeight: "bold",
    fontSize: 18,
    padding: "20px 0px 20px 0px",
    [theme.breakpoints.only("xs")]: {
      height: "20px",
    },
  },
  body: {
    padding: "30px 30px",
    paddingBottom: "50px",
  },

  dividers: {
    margin: "30px 6px 6px",
    width: "100%",
  },
  btn: {
    margin: theme.spacing(1),
    color: "black",
    backgroundColor: "white",
    boxShadow: "0px 5px 10px #00000014",
    border: "2px solid #EAEAEA",
    borderRadius: "8px",
    opacity: "1",
    marginBottom: "20px",
    "&:hover": {
      color: "white",
    },
  },
  tableedit: {
    "& .MuiPaper-root": {
      "& .MTableToolbar-root-42": {
        display: "none",
      },
    },
  },
  TextField: {
    width: "100%",
  },
  uploadlistall: {
    marginTop: 14,
    "& span": {
      paddingLeft: 20,
      fontSize: 15,
      fontWeight: 500,
    },
  },
  export: {
    position: "relative",
    backgroundColor: theme.palette.background.paper,
    border: "1px solid #4BCD3E",
    borderRadius: 30,
    // opacity: 0.9,
    display: "flex",
    padding: "4px 16px",
    fontSize: 15,
    cursor: "pointer",
    width: 60,
    float: "right",
    [theme.breakpoints.only("xs")]: {
      marginTop: 20,
    },
  },

  input: {
    width: "100%",
  },

  buttons: {
    marginTop: "30px",
    [theme.breakpoints.only("md")]: {
      marginTop: "20px",
    },
    [theme.breakpoints.only("xs")]: {
      marginTop: "0px",
      marginLeft: "0px",
    },
    [theme.breakpoints.only("sm")]: {
      marginTop: "-25px",
      marginLeft: "0px",
    },
  },
  text_title1: {
    padding: "0px 0px 4px 0px",
    opacity: 0.9,
    color: "#4BCD3E",
    width: "90px",
  },
  otpbox: {
    marginLeft: "18px",
    [theme.breakpoints.only("md")]: {
      marginTop: "18px",
    },
    [theme.breakpoints.only("xs")]: {
      marginTop: "0px",
      marginLeft: "0px",
      padding: "0px",
    },
  },
  clear: {
    boxShadow: "0px 3px 6px #0049903D",
    border: `1px solid #4BCD3E`,
    opacity: 0.9,
    fontSize: 12,
    borderRadius: 8,
    fontWeight: 600,
    marginLeft: 0,
    padding: "7px 16px",
    marginTop: "15px",
    width: "95px",
    [theme.breakpoints.only("xs")]: {
      width: "95px",
      marginLeft: 0,
      marginTop: "0px",
    },
    [theme.breakpoints.only("sm")]: {
      width: "95px",
      marginLeft: 0,
      marginTop: "0px",
    },
  },
  clear1: {
    boxShadow: "0px 3px 6px #0049903D",
    border: `1px solid #4BCD3E`,
    opacity: 0.9,
    fontSize: 12,
    borderRadius: 8,
    fontWeight: 600,
    marginLeft: 12,
    padding: "7px 16px",
    marginTop: "45px",
    [theme.breakpoints.only("xs")]: {
      width: "95px",
      marginLeft: 0,
      marginTop: "20px",
    },
    [theme.breakpoints.only("sm")]: {
      width: "95px",
      marginLeft: 10,
      marginTop: "0px",
    },
  },
  btns: {
    margin: "20px 0px",
  },
  clear3: {
    boxShadow: "0px 3px 6px #0049903D",
    border: `1px solid #4BCD3E`,
    opacity: 0.9,
    fontSize: 12,
    borderRadius: 8,
    fontWeight: 600,
    marginLeft: 12,
    padding: "7px 16px",
    marginTop: "35px",
    [theme.breakpoints.only("xs")]: {
      width: "55px",
      marginLeft: 0,
      marginTop: "0px",
    },
  },
  enterOTP: {
    [theme.breakpoints.down("sm")]: {
      marginTop: "20px",
      marginLeft: "10px",
    },

    [theme.breakpoints.down("xs")]: {
      padding: "0px !important",
      marginTop: "0px",
      marginLeft: "0px",
    },
  },
  submit1: {
    marginTop: "35px",
    background: "#4BCD3E",
    boxShadow: "0px 3px 6px #0049903D",
    borderRadius: 8,
    fontSize: 12,
    opacity: 0.9,
    marginLeft: "20px",
    color: "#2A2A2A",
    [theme.breakpoints.only("xs")]: {
      marginLeft: "0px",
      marginTop: "0px !important",
      marginRight: "20px",
    },
  },
  enterOTP1: {
    [theme.breakpoints.down("xs")]: {
      padding: "0px !important",
    },
  },
  clear2: {
    boxShadow: "0px 3px 6px #0049903D",
    border: `1px solid #4BCD3E`,
    opacity: 0.9,
    fontSize: 12,
    borderRadius: 8,
    fontWeight: 600,
    marginLeft: 12,
    padding: "7px 16px",
    marginTop: "35px",
    [theme.breakpoints.only("xs")]: {
      width: "95px",
      marginLeft: 0,
      marginTop: "0px",
    },
  },
  hint_title2: {
    textAlign: "end",
    color: "#4BCD3E",
    marginTop: "0px",
    [theme.breakpoints.down("xs")]: {
      marginLeft: "0px",
      marginTop: "0px !important",
    },
    [theme.breakpoints.down("sm")]: {
      marginTop: "-40px",
    },
  },

  submitbotton: {
    [theme.breakpoints.only("xs")]: {
      marginRight: "0px !important",
    },
    [theme.breakpoints.only("sm")]: {
      marginRight: "0px !important",
    },
  },
  menus: {
    float: "right",
    backgroundColor: theme.palette.mainbackground.default,
    boxShadow: "0px 3px 6px #00000029",
    borderRadius: 8,
    marginTop: 30,
    marginLeft: -17,
    position: "absolute",
    width: "100px",
    zIndex: 100,
    "& a": {
      color: "#000",
      textDecoration: "unset",
    },
  },
});

class ChangepinStatus extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cardNo: "",
      cardstatus: "",
      cardType: "",
      CardProduct: "",
      existpin: "",
      Newpin: "",
      ReNewpin: "",
      OTPNumber: "",
      mobnumber: "",
      disabled: true,
      query: "",
      disableVal: true,
    };
  }
  handledata = () => {
    debugger;
    this.context.setData({
      ...this.context,
      forgotpin: this.context.changepin,
    });
    this.props.history.push("/forgot_pin");
  };
  handleBack = () => { this.props.history.push("/changepin_table") };

  handlechange = (n, v) => {
    const state = this.state;
    this.setState({
      ...state,
      [n]: v,
    });
  };
  txtFieldChange = (e) => {
    if ((e.which >= 48 && e.which <= 57) || e.which === 45) {
      return true;
    } else {
      e.preventDefault();
      return false;
    }
  };

  apiFetchEdit = async (api, playod, n) => {
    await axios
      .post(`${config.api_url}${api}`, playod)
      .then((res) => {
        if (n === "otp") {
          this.setState({
            disabled: false,
            OTPNumber: res.data?.otp,
            otpId: res.data?.otpId,
          });
        } else if (n === "validate") {
          this.setState({ query: "success", disableVal: false });
        } else if (n === "submit") {
          this.props.history.push("/welcome");
        }
      })
      .catch((error) => {
        this.setState({
          errdata: error.message,
        });
      });
  };

  validate = () => {
    const valerrlist = ["cardno"];

    const error = {};
    let isValidate = true;
    valerrlist.map((val) => {
      if (!this.state[val]) {
        error[val] = true;
        isValidate = false;
        if (this.state[val] === 0) {
          error[val] = false;
          isValidate = true;
        }
      } else {
        error[val] = false;
      }
      return error;
    });
    this.setState({ error: { ...error } });
    return isValidate;
  };

  handlesubmit = async () => {
    // if (this.validate()) {
    const state = this.state;

    const body = {
      afe: "",
      expDate: "",
      instCode: "MYNT",
      keyReferenceCode: "",
      localDate: "",
      newPinBlock: state.ReNewpin ? state.ReNewpin : "",
      oldPinBlock: state.existpin ? state.existpin : "",
      pan: "",
      pinBlockFormat: "",
      reqId: "",
      termSeq: "",
    };
    await this.apiFetchEdit("api/v1/pin/change", body, "submit");
    // }
  };

  handleOtp = async () => {
    const body = {
      cardNumber: {
        // length: 0,
      },
      cbsCardNum: "",
      cbsErrorMsg: "",
      cbsMobNum: "",
      channelId: "",
      code: "",
      institutionId: "",
      mobileNumber: "",
      otac: "",
      otp: "",
      otpId: "",
    };
    await this.apiFetchEdit("api/v1/otp/generate", body, "otp");
  };
  handleOtpvalidate = async (n) => {
    const state = this.state;
    if (this.validate(n)) {
      const body = {
        cardNumber: state.vpan ? state.vpan : "",
        channelId: "",
        code: "",
        customerId: "",
        institutionId: "",
        mobileNumber: state.mobnumber ? state.mobnumber : "",
        otac: "",
        otp: state.OTPNumber ? state.OTPNumber : "",
        otpId: state.otpId ? state.otpId : "",
        refId: "",
        referenceNumber: "",
      };

      await this.apiFetchEdit("api/v1/otp/validate", body, "validate");
      this.setState({ query: "progress" });
    }
  };
  componentDidMount = () => {
    debugger;
    const val = this.context.changepin;
    this.setState({
      cardNo: val?.cardNo,
      cardstatus: val?.cardStatus,
      cardType: "",
      CardProduct: val?.accountDetailsResp[0].cardDetailsResp[0].cardProduct,
      mobnumber: val?.mobNo,
    });
  };

  handleDownload = () => {
    const state = this.state;
    this.setState({
      ...state,
      download: !state.download,
    });
  };

  render() {
    const { classes } = this.props;
    const { query } = this.state;
    return (
      <div className={classes.root}>
        <Container>
          <Card className={classes.card}>
            <Container>
              <div className={classes.body}>
                <CardContent className={classes.card_title}>
                  Card Details
                </CardContent>
                <Grid container spacing={4} style={{ marginTop: "20px" }}>
                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Card Number
                    </Typography>
                    <TextField
                      value={this.state.cardNo}
                      name="cardNo"
                      size="small"
                      disabled
                      onChange={(e) =>
                        this.handlechange("cardNo", e.target.value)
                      }
                      variant="outlined"
                      placeholder="cardNo"
                      className={classes.TextField}
                    />
                  </Grid>

                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Card Status
                    </Typography>
                    <TextField
                      value={this.state.cardstatus}
                      name="cardNo"
                      size="small"
                      disabled
                      onChange={(e) =>
                        this.handlechange("cardNO", e.target.value)
                      }
                      variant="outlined"
                      placeholder="Card Status"
                      className={classes.TextField}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Card Type
                    </Typography>
                    <TextField
                      value={this.state.cardType}
                      name="cardType"
                      size="small"
                      disabled
                      onChange={(e) =>
                        this.handlechange("cardType", e.target.value)
                      }
                      variant="outlined"
                      placeholder="CardType"
                      className={classes.TextField}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Card Product
                    </Typography>
                    <TextField
                      value={this.state.CardProduct}
                      name="cardProduct"
                      size="small"
                      disabled
                      onChange={(e) =>
                        this.handlechange("cardproduct", e.target.value)
                      }
                      variant="outlined"
                      placeholder="Card Product"
                      className={classes.TextField}
                    />
                  </Grid>
                </Grid>

                <div>
                  <Divider className={classes.dividers} />
                  <CardContent className={classes.card_title}>
                    Reset PIN
                  </CardContent>

                  <Grid
                    container
                    spacing={4}
                    style={{ marginTop: "30px", display: "flex" }}
                  >
                    <Grid
                      container
                      spacing={4}
                      style={{ marginTop: "0px", display: "flex" }}
                    >
                      <Grid
                        item
                        xs={12}
                        sm={6}
                        style={{
                          paddingLeft: "33px",
                          position: "relative",
                          marginTop: "-30px",
                        }}
                      >
                        <Typography
                          variant="subtitle2"
                          className={classes.text_title}
                        >
                          Enter Existing PIN
                        </Typography>
                        <TextField
                          value={this.state.existpin}
                          name="existpin"
                          size="small"
                          style={{ width: "100%" }}
                          onKeyPress={(e) => {
                            this.txtFieldChange(e);
                          }}
                          onChange={(e) =>
                            this.handlechange("existpin", e.target.value)
                          }
                          variant="outlined"
                          placeholder=" "
                          className={classes.TextField}
                        />
                      </Grid>

                      <Grid item xs={12} sm={6} style={{ paddingLeft: "20px" }}>
                        <Typography
                          variant="subtitle2"
                          className={classes.text_title}
                          style={{ display: "flex", marginTop: "15px" }}
                        >
                          Don't Remember Existing PIN? &nbsp;&nbsp;&nbsp;
                          <Typography
                            style={{ cursor: "pointer" }}
                            onClick={() => this.handledata()}
                            className={classes.text_title1}
                          >
                            Click here
                          </Typography>
                        </Typography>
                      </Grid>

                      <Grid item xs={12} sm={6} style={{ paddingLeft: "33px" }}>
                        <Typography
                          variant="subtitle2"
                          className={classes.text_title}
                        >
                          Enter New PIN
                        </Typography>
                        <TextField
                          value={this.state.Newpin}
                          name="Newpin"
                          size="small"
                          style={{ width: "100%" }}
                          onKeyPress={(e) => {
                            this.txtFieldChange(e);
                          }}
                          onChange={(e) =>
                            this.handlechange("Newpin", e.target.value)
                          }
                          variant="outlined"
                          placeholder=""
                          className={classes.TextField}
                        />
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <Typography
                          variant="subtitle2"
                          className={classes.text_title}
                        >
                          Re-Enter New PIN
                        </Typography>
                        <TextField
                          value={this.state.ReNewpin}
                          name="ReNewpin"
                          size="small"
                          style={{ width: "97%" }}
                          onKeyPress={(e) => {
                            this.txtFieldChange(e);
                          }}
                          onChange={(e) =>
                            this.handlechange("ReNewpin", e.target.value)
                          }
                          variant="outlined"
                          placeholder=" "
                          className={classes.TextField}
                        />
                      </Grid>
                    </Grid>
                    <Grid
                      item
                      container
                      xs={12}
                      sm={6}
                      spacing={2}
                      style={{ display: "flex" }}
                    >
                      <Grid item xs={12} sm={12} md={7} lg={10}>
                        <Typography
                          variant="subtitle2"
                          className={classes.text_title}
                        >
                          Mobile number
                        </Typography>
                        <TextField
                          size="small"
                          variant="outlined"
                          disabled
                          placeholder=""
                          value={this.state.mobnumber}
                          //   value={usertype}
                          onChange={(e) =>
                            this.handleChange("mobnumber", e.target.value)
                          }
                          className={classes.input}
                        />
                      </Grid>
                      <Grid
                        item
                        xs={12}
                        sm={6}
                        md={5}
                        lg={2}
                        className={classes.buttons}
                      >
                        <Button
                          className={classes.clear}
                          onClick={() => this.handleOtp()}
                        >
                          Send OTP
                        </Button>
                      </Grid>
                    </Grid>
                    <Grid
                      item
                      container
                      xs={12}
                      sm={6}
                      spacing={2}
                      className={classes.otpbox}
                    >
                      <Grid
                        item
                        xs={12}
                        sm={12}
                        md={6}
                        lg={9}
                        className={classes.enterOTP}
                      >
                        <Typography
                          variant="subtitle2"
                          className={classes.text_title}
                        >
                          Enter OTP Number
                        </Typography>
                        <TextField
                          size="small"
                          variant="outlined"
                          style={{ width: "100%" }}
                          placeholder="OTP Number"
                          className={classes.input}
                          onKeyPress={(e) => {
                            this.txtFieldChange(e);
                          }}
                          onChange={(e) => {
                            this.handlechange("", e.target.value);
                          }}
                          value={this.state.OTPNumber}
                        />
                        {query === "success" ? (
                          <Fab aria-label="save" size="small" color="primary">
                            <CheckIcon />
                          </Fab>
                        ) : (
                          <Fade
                            in={query === "progress"}
                            style={{
                              transitionDelay:
                                query === "progress" ? "800ms" : "0ms",
                            }}
                            unmountOnExit
                          >
                            <CircularProgress size="2rem" />
                          </Fade>
                        )}
                      </Grid>

                      <Grid
                        item
                        xs={12}
                        sm={12}
                        md={6}
                        lg={3}
                        className={classes.enterOTP1}
                      >
                        <Button
                          disabled={this.state.disabled}
                          className={classes.clear1}
                          onClick={() => this.handleOtpvalidate()}
                        >
                          validate
                        </Button>
                      </Grid>

                      <Grid item xs={12} sm={6} style={{ paddingTop: "0px" }}>
                        <Hidden xsUp>Enter OTP Number</Hidden>
                      </Grid>
                      <Grid
                        item
                        xs={12}
                        sm={6}
                        className={classes.enterOTP1}
                        style={{ paddingTop: "0px" }}
                      >
                        <Typography
                          variant="subtitle2"
                          className={classes.hint_title2}
                        >
                          Resend OTP
                          <ArrowRightIcon
                            style={{ position: "relative", top: "7px" }}
                          />
                        </Typography>
                      </Grid>
                    </Grid>
                    <Grid
                      container
                      direction="row"
                      justify="space-between"
                      alignItems="end"
                      className={classes.btns}
                    >
                      <Grid
                        item
                        xs={12}
                        sm={12}
                        className={classes.submitbotton}
                        style={{ textAlign: "end", marginRight: "190px" }}
                      >
                        <Button
                          variant="contained"
                          disabled={this.state.disableVal}
                          className={classes.submit1}
                          onClick={() => this.handlesubmit()}
                        >
                          SUBMIT
                        </Button>
                        <Button
                          className={classes.clear2}
                        // onClick={() => this.handleClear()}
                        >
                          CLEAR
                        </Button>
                      </Grid>
                    </Grid>
                  </Grid>
                </div>
              </div>
            </Container>
          </Card>
          <div style={{ float: "right" }}>
            <Button
              variant="contained"
              color="primary"
              size="medium"
              className={classes.btn}
              onClick={() => this.handleBack()}
            >
              <KeyboardBackspaceIcon /> Back
            </Button>
          </div>
        </Container>
      </div>
    );
  }
}
ChangepinStatus.contextType = EdittablesContext;
export default withStyles(styles)(withRouter(ChangepinStatus));
