        if (res.data) {
          const valData = res?.data;
          const value = valData?.customerEnquiryRes.custDetailsResp.map((v) => {
            v.CardNo = 'XXXXXXXXXXXX' + v.accountDetailsResp[0].cardDetailsResp[0].panDisplay.slice(7)
            // v.cardNo = v.accountDetailsResp[0].cardDetailsResp[0].vpan;
            v.cardStatus =
              v.accountDetailsResp[0].cardDetailsResp[0].cardStatus;
            v.cardIssueddate =
              v.accountDetailsResp[0].cardDetailsResp[0].expDate;

            v.CardRegistrationDate = "";
            return v;
          });
          this.setState({
            ...this.state,
            data: value,
          });
        }
      })
      .catch((error) => {
        this.setState({
          errdata: error.message,
        });
      });
  }
  // end

  handlesubmit = () => { };
  onCancel = () => {
    this.fetchTable();
    this.Clear();
  };

  render() {
    const { classes } = this.props;

    const { data } = this.state;
    // const menuDataMapped =
    //   menu.length > 0
    //     ? menu.map(v => {
    //         if (v) {
    //           return {
    //             value: v?.name,
    //             label: v?.url,
    //             child: v?.childrens
    //           }
    //         } else {
    //           return { value: 'nodata' }
    //         }
    //       })
    //     : []
    return (
      <div className={classes.root}>
        <Container>
          <Card className={classes.card} variant="outlined">
            {/* table Component */}
            <CardContent className={classes.card_title}>
              card Status
            </CardContent>
            <br />
            <div style={{ background: "#F4F5F8", padding: 2, borderRadius: 4 }}>
              {" "}
              <MaterialTable
                className={classes.table}
                title={false}
                options={{
                  search: false,
                  sorting: true,
                  pageSize: 5,

                  pageSizeOptions: [5, 10, 50, 100, 200],
                  actionsColumnIndex: -1,
                  rowStyle: (rowData) => ({
                    backgroundColor:
                      rowData.tableData.id % 2 === 0 && "#F8F8FA",
                  }),
                }}
                columns={[
                  {
                    field: "cardNo",
                    title: "Card Number",
                  },
                  {
                    field: "cardStatus",
                    title: "Card Status",
                  },

                  {
                    field: "cardIssueddate",
                    title: "Card Issued Date",
                  },
                  {
                    field: "CardRegistrationDate",
                    title: "Card Registration Date",
                  },
                ]}
                data={data}
                actions={[
                  (rowData) => ({
                    icon: () => (
                      <VisibilityIcon
                        onClick={() => this.handledata(rowData)}
                        fontSize="small"
                        style={{ color: "#728691" }}
                      />
                    ),

                    tooltip: "View",
                  }),
                ]}
              // com
              />
            </div>
            {/* end */}
            <br />
          </Card>
          {/* end */}
        </Container>
      </div>
    );
  }
}
