const loginUser = localStorage.getItem('user_decode') ? JSON.parse(atob(localStorage.getItem('user_decode'))) : {};
class Balanceinfotable extends React.Component {
  // state
  constructor(props) {
    super(props);
    this.state = {
      cardNo: " ",
      Totbalance: "",
      currtype: "",
      data: [],
      errdata: "",
    };
  }
  // end
  Clear = () => {
    this.setState({});
  };
  // input handle change funcy
  handlechange = (n, v) => {
    const state = this.state;
    this.setState({
      ...state,
      [n]: v,
      filtererr: false,
    });
  };
  // end

  handleedit = (val) => {
    debugger;
    this.setState({
      cardNo: val[0]?.CardNo,
      Totbalance: val[0]?.accountDetailsResp[0].avlBalance,
      currtype: val[0]?.accountDetailsResp[0].currencyCode,
    });
  };

  componentDidMount() {
    debugger;

    this.setState({
      ...this.state,
      instCode: loginUser?.instCode ?? '',
    })
    const body = {
      accCurr: "",
      accNo: "",
      clearPan: "",
      custCode: "",
      email: "",
      endRecordNo: "",
      firstName: "",
      idNumber: "",
      instCode: loginUser?.instCode ?? '',
      isCVV2Reqired: "",
      isClearPANRequired: "",
      isSHA256PANRequired: "",
      lastName: "",
      mobNo: '9788331203',
      pFlag: "s",
      reqId: "",
      reqMsgId: "",
      seqNo: "",
      startRecordNo: "",
      vpan: "",
    };
    axios
      .post(`${config.api_url}api/v1/customer/getdetails`, body)
      .then((res) => {
        if (res.data) {
          const valData = res?.data;
          const value = valData?.customerEnquiryRes.custDetailsResp.map((v) => {
            v.CardNo = v.accountDetailsResp[0].cardDetailsResp[0].vpan;
            v.cardStatus =
              v.accountDetailsResp[0].cardDetailsResp[0].cardStatus;
            v.cardIssueddate =
              v.accountDetailsResp[0].cardDetailsResp[0].expDate;

            v.CardRegistrationDate = "";
            return v;
          });
          this.setState({
            ...this.state,
            data: value,
          });
        }
      })
      .catch((error) => {
        this.setState({
          errdata: error.message,
        });
      });
  }


  handleClear = () => {
    const body = {
      instCode: loginUser?.instCode ?? '',
      isCVV2Reqired: "",
      isClearPANRequired: "",
      isSHA256PANRequired: "",
      lastName: "",
      mobNo: '9788331203',
      pFlag: "s",
      vpan: "",
    };
    axios
      .post(`${config.api_url}api/v1/customer/getdetails`)
      .then((res) => {
        if (res.data) {
          const valDatas = res?.data;
          const value = valDatas?.customerEnquiryRes.custDetailsResp.map((v) => {
            return v;
          });
          this.setState({
            ...this.state,
            data: value,
          });
        }
      })

    axios
      .post(`${config.api_url}api/v1/gettransactiondetails`, {
        "instCode": "ERUT",
        "pFlag": "",
        "reqId": "",
        "vpan": "",
        "clearPan": "",
        "accNo": "",
        "rrNo": "",
        "startDate": "",
        "reqMsgId": "",
        "endDate": "",
        "startRecordNo": "",
        "endRecordNo": ""
      })
      .then((res) => {
        if (res.data) {
          const valData = res?.data;

          this.setState({
            ...this.state,
            // data: value,
          });
        }
      })
      .catch((error) => {
        this.setState({
          errdata: error.message,
        });
      });
  }

  handleChange = (n, v) => {
    const state = this.state;
    state.error[n] = false;
    this.setState({
      [n]: v,
    });
  };

  dateval = (e) => {
    var keycode = e.which ? e.which : e.keyCode;
    if (keycode <= 48 && keycode >= 57) {
      return true;
    } else {
      e.preventDefault();
      return false;
    }
  };
  render() {
    const { classes } = this.props;

    const { data, error, date, cardNo, Totbalance, currtype } = this.state;

    return (
      <div className={classes.root}>
        <Container>
          <Card className={classes.card} variant="outlined">
            {/* table Component */}
            <CardContent className={classes.card_title}>
              View Account Balance
            </CardContent>
            <br />
            <div style={{ background: "#F4F5F8", padding: 2, borderRadius: 4 }}>
              {" "}
              <MaterialTable
                className={classes.table}
                title={false}
                options={{
                  selection: true,
                  search: false,
                  sorting: true,
                  pageSize: 5,
                  pageSizeOptions: [5, 10, 50, 100, 200],
                  actionsColumnIndex: -1,
                  rowStyle: (rowData) => ({
                    backgroundColor:
                      rowData.tableData.id % 2 === 0 && "#F8F8FA",
                  }),
                }}
                columns={[
                  {
                    field: "CardNo",
                    title: "Card Number",
                  },
                  {
                    field: "cardStatus",
                    title: "Card Status",
                  },

                  {
                    field: "cardIssueddate",
                    title: "Card Issued Date",
                  },
                  {
                    field: "CardRegistrationDate",
                    title: "Card Registration Date",
                  },
                ]}
                data={data}
                onSelectionChange={(rows) => this.handleedit(rows)}
              />
            </div>
            {/* end */}
            <br />

            <Container>
              <div className={classes.body}>
                <Grid container spacing={4}>
                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Card Number
                    </Typography>
                    <TextField
                      size="small"
                      name="cardnumber"
                      helperText={
                        error?.cardNo && "Please enter your card Number"
                      }
                      // onKeyPress={(e) => this.textvalidation(e)}
                      error={error?.cardNo}
                      onChange={(e) =>
                        this.handleChange("cardnumber", e.target.value)
                      }
                      variant="outlined"
                      placeholder=""
                      style={{ width: "100%" }}
                      value={cardNo}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Total Available Balance
                    </Typography>
                    <TextField
                      size="small"
                      name="Totalbalance"
                      helperText={error?.Totbalance && ""}
                      error={error?.Totbalance}
                      // onKeyPress={(e) => this.textvalidation(e)}
                      onChange={(e) =>
                        this.handleChange("totbalance", e.target.value)
                      }
                      variant="outlined"
                      placeholder=""
                      style={{ width: "100%" }}
                      value={Totbalance}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <Typography
                      variant="subtitle2"
                      className={classes.text_title}
                    >
                      Currency Type
                    </Typography>
                    <TextField
                      size="small"
                      name="Totalbalance"
                      helperText={error?.financialEntity && ""}
                      error={error?.financialEntity}
                      // onKeyPress={(e) => this.textvalidation(e)}
                      onChange={(e) =>
                        this.handleChange("currtype", e.target.value)
                      }
                      variant="outlined"
                      placeholder=""
                      style={{ width: "100%" }}
                      value={currtype}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                      <Typography
                        variant="subtitle2"
                        className={classes.text_title}
                      >
                        Date
                      </Typography>
                      <KeyboardDatePicker
                        style={{ width: "100%", margin: 0 }}
                        inputVariant="outlined"
                        margin="normal"
                        format="MM/dd/yyyy"
                        // disabled={checkerDisabled ? true : false}
                        value={date}
                        name="date"
                        maxDate={new Date()}
                        onChange={(e) => this.handleChange("date", e)}
                        placeholder="Date"
                        onKeyPress={(e) => this.dateval(e)}
                        KeyboardButtonProps={{
                          "aria-label": "change date",
                        }}
                        size="small"
                      />
                    </MuiPickersUtilsProvider>
                  </Grid>
                  <Grid item xs={12} sm={12} style={{ textAlign: "end" }}>
                    <Button
                      className={classes.clear1}
                      onClick={() => this.handleClear()}
                    >
                      Click to view transaction details
                    </Button>
                  </Grid>
                </Grid>
              </div>
            </Container>
          </Card>
          {/* end */}
        </Container>
      </div>
    );
  }
}
Balanceinfotable.contextType = EdittablesContext;
export default withStyles(styles)(withRouter(Balanceinfotable));
