const loginUser = localStorage.getItem('user_decode') ? JSON.parse(atob(localStorage.getItem('user_decode'))) : {};

class SetpinInfotable extends React.Component {
  // state
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  // end

  handledata = (val) => {
    this.context.setData({ ...this.context, setPin: val });
    this.props.history.push("/setpin_status");
  };
  handleedit = async (val) => { };
  fetchTable = () => { };

  componentDidMount() {
    debugger;

    const body = {
      accCurr: "",
      accNo: "",
      clearPan: "",
      custCode: "",
      email: "",
      endRecordNo: "",
      firstName: "",
      idNumber: "",
      instCode: loginUser?.[0]?.instCode ?? "",
      isCVV2Reqired: "",
      isClearPANRequired: "",
      isSHA256PANRequired: "",
      lastName: "",
      // mobNo: "9788331203",
      pflag: "s",
      reqId: "",
      reqMsgId: "",
      seqNo: "",
      startRecordNo: "",
      vpan: loginUser?.[0]?.vpanNo ?? "",
    };
    axios
      .get(`${config.api_url}api/v1/users/getAllRegisterCardDetail/${loginUser?.[0]?.userId}`)
      .then((res) => {
        if (res.data) {
          const valData = res?.data;
          const value = valData?.[0]?.customerEnquiryRes?.custDetailsResp?.map((v) => {
            v.cardNo = v?.accountDetailsResp?.[0]?.cardDetailsResp?.[0]?.panDisplay?.slice?.(0, 6) + "XXXXXX" + v?.accountDetailsResp?.[0]?.cardDetailsResp?.[0]?.panDisplay?.slice?.(7)
            // loginUser?.[0]?.truncated_card_no?.slice?.(0, 6) + "XXXXXX" + loginUser?.[0]?.truncated_card_no?.slice?.(7);
            // v.accountDetailsResp[0].cardDetailsResp[0].vpan;
            v.cardStatus = v?.accountDetailsResp?.[0]?.cardDetailsResp?.[0]?.cardStatus;
            v.cardIssueddate = v?.accountDetailsResp?.[0]?.cardDetailsResp?.[0]?.expDate;

            v.CardRegistrationDate = "";
            return v;
          });
          this.setState({
            ...this.state,
            data: value,
          });
        }
      })
      .catch((error) => {
        this.setState({
          errdata: error.message,
        });
      });
  }
  // end

  handlesubmit = () => { };

  render() {
    const { classes } = this.props;

    const { data } = this.state;

    return (
      <div className={classes.root}>
        <Container>
          <Card className={classes.card} variant="outlined">
            {/* table Component */}
            <CardContent className={classes.card_title}>
              card Status
            </CardContent>
            <br />
            <div style={{ background: "#F4F5F8", padding: 2, borderRadius: 4 }}>
              {" "}
              <MaterialTable
                className={classes.table}
                title={false}
                options={{
                  search: false,
                  sorting: true,
                  pageSize: 5,

                  pageSizeOptions: [5, 10, 50, 100, 200],
                  actionsColumnIndex: -1,
                  rowStyle: (rowData) => ({
                    backgroundColor:
                      rowData.tableData.id % 2 === 0 && "#F8F8FA",
                  }),
                }}
                columns={[
                  {
                    field: "cardNo",
                    title: "Card Number",
                  },
                  {
                    field: "cardStatus",
                    title: "Card Status",
                  },
                  // {
                  //     field: "userId",
                  //     title: "User Id",
                  // },
                  {
                    field: "cardIssueddate",
                    title: "Card Issued Date",
                  },
                  {
                    field: "CardRegistrationDate",
                    title: "Card Registration Date",
                  },
                ]}
                data={data}
                actions={[
                  (rowData) => ({
                    icon: () => (
                      <VisibilityIcon
                        onClick={() => this.handledata(rowData)}
                        fontSize="small"
                        style={{ color: "#728691" }}
                      />
                    ),

                    tooltip: "View",
                  }),
                ]}
              // com
              />
            </div>
            {/* end */}
            <br />
          </Card>
          {/* end */}
        </Container>
      </div>
    );
  }
}
SetpinInfotable.contextType = EdittablesContext;
export default withStyles(styles)(withRouter(SetpinInfotable));
